#!/usr/bin/env bash # ignoring pipe fail of non-zero exit code set -o pipefail docker images --no-trunc | grep '&lt;none&gt;' | awk '{ print $3 }' | xargs -r docker rmi docker ps --filter status=dead --filter status=exited -aq | xargs docker rm -v [ ! -z &quot;$VM&quot; ] &amp;&amp; ssh $VM 'bash -s' &lt; docker-clean.sh.sh  kube build export K8S_VERSION = v1.13.3 git clone https://github.com/kubernetes/kubernetes.git $GOPATH/src/K8S.io/ git fetch --all git checkout tags/$K8S_VERSION -b v$K8S_VERSION  #!/usr/bin/env bash export ETCD_HOST=192.168.1.9 export KUBE_INTEGRATION_ETCD_URL=http://$ETCD_HOST:2379 cd $GOPATH/src/K8S.io/kubernetes/build/ bash -x ./run.sh make &gt; run.log 2&gt;&amp;1  kubeadm init #!/usr/bin/env bash cat &lt;&lt; EOF &gt; init.sh #!/usr/bin/env bash sudo kubeadm reset -f sudo kubeadm init --kubernetes-version=v1.13.3 --pod-network-cidr 10.2.0.0/16 -v 4 &gt; kubeadm.init.log 2&gt;&amp;1 mkdir -p $HOME/.kube sudo cp -f /etc/kubernetes/admin.conf $HOME/.kube/config sudo chown $(id -u):$(id -g) $HOME/.kube/config EOF ssh $VM 'bash -s' &lt; init.sh rm init.sh  kube image pull then tag #!/usr/bin/env bash docker pull mirrorgooglecontainers/kube-apiserver:v1.13.3 docker pull mirrorgooglecontainers/kube-controller-manager:v1.13.3 docker pull mirrorgooglecontainers/kube-scheduler:v1.13.3 docker pull mirrorgooglecontainers/kube-proxy:v1.13.3 docker pull mirrorgooglecontainers/pause:3.1 docker pull mirrorgooglecontainers/etcd:3.2.24 docker pull coredns/coredns:1.2.6 docker tag mirrorgooglecontainers/kube-apiserver:v1.13.3 K8S.gcr.io/kube-apiserver:v1.13.3 docker tag mirrorgooglecontainers/kube-controller-manager:v1.13.3 K8S.gcr.io/kube-controller-manager:v1.13.3 docker tag mirrorgooglecontainers/kube-scheduler:v1.13.3 K8S.gcr.io/kube-scheduler:v1.13.3 docker tag mirrorgooglecontainers/kube-proxy:v1.13.3 K8S.gcr.io/kube-proxy:v1.13.3 docker tag mirrorgooglecontainers/pause:3.1 K8S.gcr.io/pause:3.1 docker tag mirrorgooglecontainers/etcd:3.2.24 K8S.gcr.io/etcd:3.2.24 docker tag coredns/coredns:1.2.6 K8S.gcr.io/coredns:1.2.6  prepare kubelet for kubeadm deploy  build
cd build run.sh make scp ~/go/src/K8S.io/kubernetes/_output/dockerized/bin/linux/amd64/kube??? vm1:~/   deploy K8S master #!/usr/bin/env bash if [ ! -z &quot;$VM&quot; ]; then VM = t1 echo &quot;VAR VM is not set&quot; exit fi if [ ! -z &quot;$KV&quot; ]; then KV = v1.13.3 echo &quot;VAR VM is not set ,set to $KV&quot; exit fi if [ ! -z &quot;$PN&quot; ]; then PN = 10.2.0.0/16 echo &quot;VAR PN is not set, set to $PN&quot; exit fi scp ~/go/src/K8S.io/kubernetes/build/debs/kubelet.service $VM:~/ scp ~/go/src/K8S.io/kubernetes/build/debs/10-kubeadm.conf $VM:~/ scp ~/go/src/github.com/containernetworking/cni/bin/* $VM:~/ scp ~/go/bin/cri* $VM:~/ cat &lt;&lt; EOF &gt; d.sh #!/usr/bin/env bash sudo cp ~/kube??? /usr/bin/ sudo cp ~/kubelet.service /etc/systemd/system/kubelet.service sudo mkdir -p /etc/kubernetes/manifests sudo mkdir -p /etc/systemd/system/kubelet.service.d/ sudo cp ~/10-kubeadm.conf /etc/systemd/system/kubelet.service.d/10-kubeadm.conf sudo systemctl daemon-reload sudo systemctl enable kubelet --now sudo systemctl start kubelet mkdir -p /opt/cni/bin sudo cp ~/cnitool ~/noop /opt/cni/bin sudo cp ~/cri* /usr/local/bin/ #clean up rm -f kube??? crictl critest cnitool noop kubelet.service 10-kubeadm.conf # sudo kubeadm init --kubernetes-version=$KV --pod-network-cidr 10.2.0.0/16 -v 4 if [ -d &quot;$HOME/.kube&quot; ]; then mkdir -p $HOME/.kube fi sudo cp -f /etc/kubernetes/admin.conf $HOME/.kube/config sudo chown $(id -u):$(id -g) $HOME/.kube/config curl https://docs.projectcalico.org/v3.5/getting-started/kubernetes/installation/hosted/calico.yaml&gt; calico.yaml # calico etcd setup sed -i -e &quot;s/\(^etcd_endpoints: \&quot;http.*$\)/etcd_endpoints: \&quot;https:\/\/$VM:2379\&quot;/g&quot; calico.yaml # etcd_ca: &quot;/calico-secrets/etcd-ca&quot; sed -i -e 's/etcd_ca: \&quot;\&quot; \# \&quot;\/calico-secrets/etcd-ca\&quot;/etcd_ca: \&quot;\/calico-secrets\/etcd-ca\&quot;/g' calico.yaml sed -i -e 's/etcd_cert: \&quot;\&quot; # \&quot;\/calico-secrets\/etcd-cert\&quot;/etcd_cert: \&quot;\/calico-secrets\/etcd-cert\&quot;/g' calico.yaml sed -i -e 's/etcd_key: \&quot;\&quot; # \&quot;\/calico-secrets\/etcd-key\&quot;/etcd_key: \&quot;\/calico-secrets\/etcd-key\&quot;/g' calico.yaml CA=$(cat /etc/kubernetes/pki/etcd/ca.crt | base64 -w 0) CERT=$(cat /etc/kubernetes/pki/etcd/server.crt | base64 -w 0) KEY=$(sudo cat /etc/kubernetes/pki/etcd/server.key | base64 -w 0) sed -i -e &quot;s/# etcd-ca: null/etcd-ca: $CA/g&quot; calico.yaml sed -i -e &quot;s/# etcd-cert: null/etcd-cert: $CERT/g&quot; calico.yaml sed -i -e &quot;s/# etcd-key: null/etcd-key: $KEY/g&quot; calico.yaml kubectl apply -f calico.yaml EOF # execute the local script on the remote server ssh $VM 'bash -s' &lt; d.sh rm d.sh  deploy K8S working node #!/usr/bin/env bash if [ ! -z &quot;$VM&quot; ]; then VM = t1 echo &quot;VAR VM is not set&quot; exit fi scp ~/go/src/K8S.io/kubernetes/build/debs/kubelet.service $VM:~/ scp ~/go/src/K8S.io/kubernetes/build/debs/10-kubeadm.conf $VM:~/ scp ~/go/src/github.com/containernetworking/cni/bin/* $VM:~/ scp ~/go/bin/cri* $VM:~/ cat &lt;&lt; EOF &gt; d.sh #!/usr/bin/env bash sudo cp ~/kube??? /usr/bin/ sudo cp ~/kubelet.service /etc/systemd/system/kubelet.service sudo mkdir -p /etc/kubernetes/manifests sudo mkdir -p /etc/systemd/system/kubelet.service.d/ sudo cp ~/10-kubeadm.conf /etc/systemd/system/kubelet.service.d/10-kubeadm.conf sudo systemctl daemon-reload sudo systemctl enable kubelet --now sudo systemctl start kubelet mkdir -p /opt/cni/bin sudo cp ~/cnitool ~/noop /opt/cni/bin sudo cp ~/cri* /usr/local/bin/ #clean up rm -f kube??? crictl critest cnitool noop kubelet.service 10-kubeadm.conf EOF TOKEN=$(kubeadm token list) CA_HASH=$(openssl x509 -pubkey -in /etc/kubernetes/pki/ca.crt | openssl rsa -pubin -outform der 2&gt;/dev/null | \ openssl dgst -sha256 -hex | sed 's/^.* //') cat &lt;&lt; EOF &gt; d.sh #!/usr/bin/env bash kubeadm join 192.168.1.11:6443 --token $TOKEN --discovery-token-ca-cert-hash sha256:$CA_HASH EOF # 